import { Component, OnInit } from "@angular/core";

@Component({
  selector: 'nol-map-graticule-example',
  template: `
    <nol-map height="500px">
      <nol-view [center]="[0, 0]" [zoom]="2"></nol-view>
      <nol-tile-layer>
        <nol-osm-source></nol-osm-source>
      </nol-tile-layer>
      <nol-graticule-layer [showLabels]="true"></nol-graticule-layer>
    </nol-map>
  `
})
export class NolMapGraticuleExampleComponent implements OnInit {

  constructor() { }

  ngOnInit(): void { }

}

import { CommonModule } from "@angular/common";
import { NgModule } from "@angular/core";
import { NolMapModule } from "ngx-ol-library/map";
import { NolViewModule } from "ngx-ol-library/view";
import { NolGraticuleLayerModule } from "ngx-ol-library/layers/graticule-layer";
import { NolTileLayerModule } from "ngx-ol-library/layers/tile-layer";
import { NolOSMSourceModule } from "ngx-ol-library/sources/osm-source";
import { NolMapGraticuleExampleComponent } from "./map-graticule/map-graticule.component";

@NgModule({
  declarations: [
    NolMapGraticuleExampleComponent
  ],
  imports: [
    CommonModule,
    NolMapModule,
    NolViewModule,
    NolTileLayerModule,
    NolGraticuleLayerModule,
    NolOSMSourceModule
  ]
})
export class NolGraticuleLayerExampleModule {}
