import { Component } from '@angular/core';
import { MapBrowserEvent } from 'ol';
import { Coordinate } from 'ol/coordinate';

@Component({
  selector: 'nol-overlay-example',
  template:  `
    <nol-map height="500px" (onClick)="onClickMap($event)">
      <nol-view [center]='[0, 0]' [zoom]='2'></nol-view>
      <nol-tile-layer>
        <nol-osm-source></nol-osm-source>
      </nol-tile-layer>
      <!-- Popup showing the position the user clicked -->
      <nol-overlay
        *ngIf="clickPosition"
        [position]="clickPosition"
        [positioning]="'bottom-center'"
      >
        <ng-container *nolOverlayContent>
          <div class="popover">
            <div class="popover-arrow"></div>
            <h3 class="popover-header">Welcome to OpenLayers</h3>
            <div class="popover-body">
              <p>The location you clicked was:</p>
              <code>{{ clickPosition | nolToStringHDMS }}</code>
            </div>
          </div>
        </ng-container>
      </nol-overlay>
      <!-- Vienna marker -->
      <nol-overlay
        [position]="[16.3725, 48.208889] | nolFromLonLat"
        positioning="center-center"
        [stopEvent]="false"
      >
        <ng-container *nolOverlayContent>
          <div class="vienna-marker"></div>
        </ng-container>
      </nol-overlay>
      <!-- Vienna label -->
      <nol-overlay [position]="[16.3725, 48.208889] | nolFromLonLat">
        <ng-container *nolOverlayContent>
          <a class="vienna-label" href="https://en.wikipedia.org/wiki/Vienna" target="_blank">Vienna</a>
        </ng-container>
      </nol-overlay>
    </nol-map>
  `,
  styles: [`
    .popover {
      position: relative;
      background-color: #fff;
      background-clip: padding-box;
      border: 1px solid rgba(0, 0, 0, 0.175);
      border-radius: 0.5rem;
      max-width: 276px;
      transform: translateY(-8px);
    }

    .popover-arrow {
      position: absolute;
      transform: translate(129px, 0px);
      left: 0px;
      bottom: calc(0.5rem * -1 - 1px);
      display: block;
      width: 1rem;
      height: 0.5rem;
      transform: translate(129px, 0px);
    }

    .popover-arrow::before,
    .popover-arrow::after {
      position: absolute;
      display: block;
      content: "";
      border-color: transparent;
      border-style: solid;
      border-width: 0.5rem calc(1rem * .5) 0;
    }

    .popover-arrow::before {
      bottom: 0;
      border-top-color: rgba(0, 0, 0, 0.175);
    }

    .popover-arrow::after {
      bottom: 1px;
      border-top-color: #fff;
    }


    .popover-header {
      padding: 0.5rem 1rem;
      margin: 0;
      font-size: 1rem;
      color: rgba(0, 0, 0, .85);
      background-color: #f0f0f0;
      border-bottom: 1px solid rgba(0, 0, 0, 0.175);
      border-top-left-radius: calc(0.5rem - 1px);
      border-top-right-radius: calc(0.5rem - 1px);
    }

    .popover-body {
      min-width: 276px;
      padding: 1rem 1rem;
      color: #212529;
    }

    .popover-body > p {
      margin-bottom: 1rem;
    }

    .popover-body > code {
      color: #333333;
      background-color: #F5F5F5;
      padding: 0.2em 0.4em;
      border-radius: 3px;
      font-size: .875em;
    }

    .vienna-marker {
      width: 20px;
      height: 20px;
      border: 1px solid #088;
      border-radius: 10px;
      background-color: #0FF;
      opacity: 0.5;
      box-sizing: border-box;
    }

    .vienna-label {
      text-decoration: none;
      color: white;
      font-size: 11pt;
      font-weight: bold;
      text-shadow: black 0.1em 0.1em 0.2em;
    }
  `]
})
export class NolOverlayExampleComponent {

  clickPosition?: Coordinate;

  onClickMap(event: MapBrowserEvent<any>): void {
    this.clickPosition = event.coordinate;
  }
}

import { Component } from "@angular/core";
import { MapBrowserEvent } from "ol";
import { Coordinate } from "ol/coordinate";

@Component({
  selector: 'nol-popup-example',
  template: `
    <nol-map height="500px" (onSingleclick)="onSingleclickMap($event)">
      <nol-view [center]="[0, 0]" [zoom]="2"></nol-view>
      <nol-tile-layer>
        <nol-xyz-source [attributions]="attributions" [url]="url" [tileSize]="512"></nol-xyz-source>
      </nol-tile-layer>
      <!-- Create an overlay to anchor the popup to the map. -->
      <nol-overlay
        [autoPan]="{
          animation: {
            duration: 250
          }
        }"
        [position]="clickPosition"
      >
        <ng-container *nolOverlayContent>
          <div class="popup">
            <a class="popup-closer" (click)="closePopup()">âœ–</a>
            <div class="popup-content">
              <p>You clicked here:</p>
              <code *ngIf="clickPosition">{{ clickPosition | nolToStringHDMS }}</code>
            </div>
          </div>
        </ng-container>
      </nol-overlay>
    </nol-map>
  `,
  styles: [`
    .popup {
      position: absolute;
      background-color: white;
      box-shadow: 0 1px 4px rgba(0,0,0,0.2);
      padding: 15px;
      border-radius: 10px;
      border: 1px solid #cccccc;
      bottom: 12px;
      left: -50px;
      min-width: 280px;
    }

    .popup:before,
    .popup:after {
      top: 100%;
      border: solid transparent;
      content: " ";
      height: 0;
      width: 0;
      position: absolute;
      pointer-events: none;
    }

    .popup:before {
      border-top-color: #cccccc;
      border-width: 11px;
      left: 48px;
      margin-left: -11px;
    }

    .popup:after {
      border-top-color: white;
      border-width: 10px;
      left: 48px;
      margin-left: -10px;
    }

    .popup-closer {
      text-decoration: none;
      position: absolute;
      top: 2px;
      right: 8px;
      cursor: pointer;
    }

    .popup-content > p {
      margin-bottom: 1rem;
    }

    .popup-content > code {
      color: #333333;
      background-color: #F5F5F5;
      padding: 0.2em 0.4em;
      border-radius: 3px;
      font-size: .875em;
    }
  `]
})
export class NolPopupExampleComponent {

  attributions = `<a href="https://www.maptiler.com/copyright/" target="_blank">&copy; MapTiler</a> <a href="https://www.openstreetmap.org/copyright" target="_blank">&copy; OpenStreetMap contributors</a>`;
  url = 'https://api.maptiler.com/maps/streets/{z}/{x}/{y}.png?key=get_your_own_D6rA4zTHduk6KOKTXzGB';
  clickPosition?: Coordinate;

  onSingleclickMap(event: MapBrowserEvent<any>): void {
    this.clickPosition = event.coordinate;
  }

  closePopup(): void {
    this.clickPosition = undefined;
  }
}

import { CommonModule } from '@angular/common';
import { NgModule } from '@angular/core';
import { NolCoordinateModule } from 'ngx-ol-library/coordinate';
import { NolTileLayerModule } from 'ngx-ol-library/layers/tile-layer';
import { NolMapModule } from 'ngx-ol-library/map';
import { NolOverlayModule } from 'ngx-ol-library/overlay';
import { NolProjModule } from 'ngx-ol-library/proj';
import { NolOSMSourceModule } from 'ngx-ol-library/sources/osm-source';
import { NolXYZSourceModule } from 'ngx-ol-library/sources/xyz-source';
import { NolViewModule } from 'ngx-ol-library/view';
import { NolOverlayExampleComponent } from './overlay/overlay.component';
import { NolPopupExampleComponent } from './popup/popup.component';

@NgModule({
  declarations: [
    NolOverlayExampleComponent,
    NolPopupExampleComponent
  ],
  imports: [
    CommonModule,
    NolMapModule,
    NolViewModule,
    NolTileLayerModule,
    NolOSMSourceModule,
    NolXYZSourceModule,
    NolOverlayModule,
    NolCoordinateModule,
    NolProjModule
  ]
})
export class NolOverlayExampleModule {}
